/* global module */var base = require('./controller/base');var user = require('./controller/user');var proxy = require('./controller/proxy');module.exports = function (app) {    app.get('/login', user.login);    app.get('/', base.validate, user.index);    app.get('/list', base.validate, user.list);    app.get('/mmenu', base.getMainMemu);    app.get('/menu', base.validate, user.list);    app.get('/logout', base.validate, user.doLogout);    app.post('/login/:username/:password', user.doLogin);    app.post('/update', base.validate, user.doUpdate);    app.post('/del', base.validate, user.doDelete);    app.post('/add', base.validate, user.doAdd);    app.post('/proxy', base.validate, proxy.proxy);    app.get('*', base.validate, user.index);};