import React, { Component, PropTypes } from 'react';import { createStore, compose, combineReducers, applyMiddleware } from 'redux';import { Provider } from 'react-redux';import { ReduxRouter, routerStateReducer, reduxReactRouter, pushState } from 'redux-router';import { createHistory } from 'history';import thunkMiddleware from 'redux-thunk';import createLogger from 'redux-logger';import routes from './Router';import root from '../reducers';import '../stylesheets/bootstrap.css';const reducer = combineReducers({    router: routerStateReducer,    root});const store = compose(    applyMiddleware(thunkMiddleware,createLogger()),    reduxReactRouter({        routes,        createHistory    }))(createStore)(reducer);class Root extends Component {    render() {        return (            <div>                <Provider store={store}>                    <ReduxRouter/>                </Provider>            </div>        );    }} export default Root;