/* global exports */var config = require('./config');/** * 存放Cookies: 两个参数 * @param {type} name cookie名 * @param {type} value 值 * @returns {undefined} */var setCookie = exports.setCookie = function (name, value) {    var Days = 1;    var exp = new Date();    exp.setTime(exp.getTime() + Days * 24 * 60 * 60 * 1000);    document.cookie = name + "=" + escape(value) + ";expires=" + exp.toGMTString() + ";path='/'";};/** * 获取cookie * @param {type} name * @returns {string} cookie的值 */var getCookie = exports.getCookie = function (name) {    var arr, reg = new RegExp("(^| )" + name + "=([^;]*)(;|$)");    if (arr = document.cookie.match(reg))        return unescape(arr[2]);    else {        location.href = config.host.login_url;        return null;    }};